(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let r=setInterval((()=>{let t=(()=>{let t=(new Date("25 june 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=t.hours.toString().padStart(2,"0"),n.textContent=t.minutes.toString().padStart(2,"0"),o.textContent=t.seconds.toString().padStart(2,"0"),t.timeRemaining<=0&&(clearInterval(r),e.textContent="0".padStart(2,"0"),n.textContent="0".padStart(2,"0"),o.textContent="0".padStart(2,"0"))}),1e3)})()})();